package com.example.pinterestclone.ui.fragment

import android.os.Bundle
import android.view.View
import android.widget.TextView
import androidx.fragment.app.Fragment
import androidx.navigation.fragment.findNavController
import androidx.recyclerview.widget.GridLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.pinterestclone.R
import com.example.pinterestclone.adapter.SearchRecAdapter
import com.example.pinterestclone.model.SearchRecModelim

class SearchFragment : Fragment(R.layout.fragment_search) {
    lateinit var list: ArrayList<SearchRecModelim>
    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        initViews(view)
    }
    private fun initViews(view: View) {
        list = loadList()
        val search=view.findViewById<TextView>(R.id.search121)

        search.setOnClickListener {
            findNavController().navigate(R.id.action_searchFragment_to_search2Fragment)
        }
        var rvSearch = view?.findViewById<RecyclerView>(R.id.rv_search1)
        val adapter = SearchRecAdapter(list)
        val layoutManager =
            GridLayoutManager(requireContext(), 2)
        rvSearch?.adapter = adapter
        rvSearch?.layoutManager = layoutManager
        adapter.yuborish={
            val bundle=Bundle()
            bundle.putString("share",it)
            bundle.putString("main","search")
            findNavController().navigate(R.id.action_searchFragment_to_search2Fragment,bundle)

        }
    }

